	  if (currentEnd == current.length && oldEnd == old.length)
	    suffixCount = sharedSuffix(current, old, minLength - prefixCount);

	  currentStart += prefixCount;
	  oldStart += prefixCount;
	  currentEnd -= suffixCount;
